SELECT countryName.name, c.recorddate, c.cases, c.deaths, c.CountOf, c.ID from (SELECT a.*, b.CountOf FROM record a JOIN (SELECT countryID, recorddate, COUNT(*) As CountOf FROM record GROUP BY countryID, recorddate HAVING COUNT(*) > 1) b ON a.countryID = b.countryID AND a.recorddate = b.recorddate ORDER BY countryID) AS c JOIN country AS countryName ON countryName.ID = c.countryID;
